import React from 'react';

const Pool = require('pg').Pool
const pool = new Pool({
  user: 'postgres',
  host: 'localhost',
  database: 'rc1',
  password: 'abricot123',
  port: 5432,
});

const test = () => {
  return new Promise(function(resolve, reject) {
    pool.query('SELECT * FROM train', (error, results) => {
      if (error) {
        reject(error)
      }
      resolve(results.rows);
    })
  }) 
}
  
const Home = () => {
  return (
    <div
      style={{
        display: 'flex',
        justifyContent: 'Right',
        alignItems: 'Right',
        height: '100vh'
      }}
    >
      <h1>Home</h1>
      <button onClick={test}>
      Select!
    </button>
    </div>
  );
};
  
export default Home;
