import L from 'leaflet';

const marker = require('./images/train.png')

const iconPerso = new L.Icon({
    iconUrl: marker,
    iconRetinaUrl: marker,
    //iconAnchor: null,
    //popupAnchor: null,
    shadowUrl: null,
    shadowSize: null,
    shadowAnchor: null,
    iconSize: new L.Point(30, 30)
});

export { iconPerso };
