import { MapContainer, useMapEvents , TileLayer, Marker, Popup} from 'react-leaflet';
import React, { useState } from "react";
import "./App.css";
import "leaflet/dist/leaflet.css";
import { iconPerso } from "./iconMarker";
import dataGares from "./data/referentiel-gares-voyageurs.json";


const center = [46.227638, 2.213749];

const filterGare1 = dataGares.filter(
  (gare) => gare.fields.segmentdrg_libelle === "a"
);

const filterGare2 = dataGares.filter(
  (gare) =>
    (gare.fields.segmentdrg_libelle === "a" ||
    gare.fields.segmentdrg_libelle === "b") &&
    gare.fields.longitude_entreeprincipale_wgs84 !== undefined
);

const filterGare3 = dataGares.filter(
  (gare) =>
    (gare.fields.segmentdrg_libelle === "a" ||
    gare.fields.segmentdrg_libelle === "b" ||
    gare.fields.segmentdrg_libelle === "c") &&
    gare.fields.longitude_entreeprincipale_wgs84 !== undefined
);


const map1 = filterGare1.map(e => (
        
  <Marker
    key={e.recordid}
    position={[e.fields.latitude_entreeprincipale_wgs84, e.fields.longitude_entreeprincipale_wgs84]}
    icon={iconPerso}
  >
    <Popup>
      <h3> {"Name : " + e.fields.gare_alias_libelle_noncontraint} </h3>
    </Popup>
</Marker>
));

const map2 = filterGare2.map(e => (
  <Marker
    key={e.recordid}
    position={[e.fields.latitude_entreeprincipale_wgs84, e.fields.longitude_entreeprincipale_wgs84]}
    icon={iconPerso}
  >
    <Popup>
      <h3> {"Name : " + e.fields.gare_alias_libelle_noncontraint} </h3>
      
    </Popup>
</Marker>
));

const map3 = filterGare3.map(e => (
  <Marker
    key={e.recordid}
    position={[e.fields.latitude_entreeprincipale_wgs84, e.fields.longitude_entreeprincipale_wgs84]}
  >
    <Popup>
      <h3> {"Name : " + e.fields.gare_alias_libelle_noncontraint} </h3>
      
    </Popup>
</Marker>
));


let niveau = filterGare1;


const ZoomTracker = ({ setZoom }) => {
  const map = useMapEvents({
    zoom(){
      setZoom(map.getZoom())
    }
  })
  return null
}


 
const ZoomControlledLayer = ({ zoom, children }) => {

  
 
  if (zoom <= 8){
    console.log("niveau 1");
    return (map1);
  } else if (zoom >= 9 && zoom <= 11){
    console.log("niveau 2");
    return (map2);
  }else if (zoom >= 12){
    console.log("niveau 3");
    return (map3);

  } else{
    console.log("Aucun niveau");
    return (map1);
  }

}




export default function App(){

  const [zoom, setZoom] = useState()

  return (

    <MapContainer 
    center={center}
    zoom={6}
    style={{ width: "100vw", height: "100vh" }}
    >
      <TileLayer
        url="https://api.maptiler.com/maps/basic/256/{z}/{x}/{y}.png?key=F4ZxF5g8ioWE3GlTx3i0#-0.2/0.00000/76.51878"
        attribution='<a href="https://www.maptiler.com/copyright/" target="_blank">&copy; MapTiler</a> <a href="https://www.openstreetmap.org/copyright" target="_blank">&copy; OpenStreetMap contributors</a>'
      />

      <ZoomTracker setZoom={setZoom} />
      <ZoomControlledLayer zoom={zoom}>
      
      </ZoomControlledLayer>
    </MapContainer>

  )

}
