import React, { useState } from "react";
import { useMapEvents } from "react-leaflet";
import "./App.css";
import "leaflet/dist/leaflet.css";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import { iconPerso } from "./iconMarker";
import { map} from "leaflet";
import L, { LeafletMouseEventHandlerFn } from "leaflet";
import dataGares from "./data/referentiel-gares-voyageurs.json";
//import "leaflet.markercluster";

const center = [46.227638, 2.213749];

const filterGare1 = dataGares.filter(
  (gare) => gare.fields.segmentdrg_libelle === "a"
);

const filterGare2 = dataGares.filter(
  (gare) =>
    (gare.fields.segmentdrg_libelle === "a" ||
      gare.fields.segmentdrg_libelle === "b") &&
    gare.fields.longitude_entreeprincipale_wgs84 !== undefined
);

const filterGare3 = dataGares.filter(
  (gare) =>
    (gare.fields.segmentdrg_libelle === "a" ||
      gare.fields.segmentdrg_libelle === "b" ||
      gare.fields.segmentdrg_libelle === "c") &&
    gare.fields.longitude_entreeprincipale_wgs84 !== undefined
);
var actualZoom;





function getLayers(){

// TEST D'AJOUT D'UN SEUL MARKER
  console.log("Ajout du marker test");
  console.log(42.5020902);
  console.log(2.1131379);

  var newMarker = new L.marker([42.5020902, 2.1131379])
  .addTo(map)
  .bindPopup("MARKER TEST");
  
// TEST D'AJOUT DE TOUS LES MARKERS DE FILTERGARE3

  // for (const e of filterGare3) {
  //   console.log("-------------");
  //   console.log(e.fields.latitude_entreeprincipale_wgs84);
  //   console.log(e.fields.longitude_entreeprincipale_wgs84);
  //    var newMarker = new L.marker([e.fields.latitude_entreeprincipale_wgs84, e.fields.longitude_entreeprincipale_wgs84])
  //    .addTo(map)
  //    .bindPopup(e.recordid);
  // }

}


function GetZoom() {
  var [zoomLevel, setZoomLevel] = useState(6); // initial zoom level provided for MapContainer

  const mapEvents = useMapEvents({
    zoomend: () => {
      setZoomLevel(mapEvents.getZoom());
    },
  });

  console.log(zoomLevel);

  actualZoom = zoomLevel;
  return zoomLevel;
}


  










export default function App() {
  

  const [, updateState] = React.useState();
  const forceUpdate = React.useCallback(() => updateState({}), []);

  const [markers, setMarkers] = React.useState([]);
  let niveau = filterGare1;




  console.log(" app : " + actualZoom);
  if (actualZoom <= 8) {
    niveau = filterGare1;
    console.log("niveau 1");
  } else if (actualZoom >= 9 && actualZoom <= 11) {
    niveau = filterGare2;
    console.log("niveau 2");

  } else if (actualZoom >= 12) {
    niveau = filterGare3;
    console.log("niveau 3");
  } else {
    niveau = filterGare1;
    console.log("aucun niveau");
  }
  


  return (
    <MapContainer
      center={center}
      zoom={6}
      style={{ width: "100vw", height: "100vh" }}
    >
      <TileLayer
        url="https://api.maptiler.com/maps/basic/256/{z}/{x}/{y}.png?key=F4ZxF5g8ioWE3GlTx3i0#-0.2/0.00000/76.51878"
        attribution='<a href="https://www.maptiler.com/copyright/" target="_blank">&copy; MapTiler</a> <a href="https://www.openstreetmap.org/copyright" target="_blank">&copy; OpenStreetMap contributors</a>'
      />

      {niveau.map((gare) => (
        <Marker
          key={gare.recordid}
          position={[
            gare.fields.latitude_entreeprincipale_wgs84,
            gare.fields.longitude_entreeprincipale_wgs84,
          ]}
          icon={iconPerso}
        >
          <Popup>
            <h3> {"Name : " + gare.fields.gare_alias_libelle_noncontraint} </h3>
            <button onClick={() => setMarkers([42.5020902, 2.1131379])}>
              Add Layers
            </button>
          </Popup>
        </Marker>
      ))}
      {markers.map((marker, i) => (
        <Marker key={`marker-${i}`} position={marker} />
      ))}

      <GetZoom />
    </MapContainer>
  );
}
