import { MapContainer, Map, useMap, useMapEvents , TileLayer, Marker, Popup, Button } from 'react-leaflet';
import { Icon } from 'leaflet'


import React, {useState, useEffect } from "react";
import L from "leaflet";
import dataGares from "../data/referentiel-gares-voyageurs.json";
import "leaflet-geosearch/dist/geosearch.css";
import { GeoSearchControl, OpenStreetMapProvider } from "leaflet-geosearch";
import marker1 from '../images/train.png';
import marker2 from '../images/marker.svg';

//CSS
import "leaflet-easybutton/src/easy-button.js";
import "leaflet-easybutton/src/easy-button.css";
import "leaflet/dist/leaflet.css";
import "font-awesome/css/font-awesome.min.css";
let tmp = 1;
let jsonData;
let stringGare = "";
let filter = [];
const RangeSlider = () => {

  const [rangeval, setRangeval] = useState(null);
  tmp = rangeval * 60;

  if(rangeval == null) {
    return (
      <div>
        <br></br>
        <label>1</label>
        <input id="slider" type="range" className="custom-range" min="1" max="10" step="1" defaultValue="1"
         onChange={(event) => setRangeval(event.target.value)} />
        <label>10</label>
        <h4>Temps : {rangeval}</h4>
      </div>
      )
  } else {
    return (
      <div>
        <br></br>
        <label>1</label>
        <input id="slider" type="range" className="custom-range" min="1" max="10" step="1" defaultValue="1"
         onChange={(event) => setRangeval(event.target.value)} />
        <label>10</label>
        <h4>Temps : {rangeval} heure(s)</h4>
      </div>
      )
  }
};

const iconBasic = new Icon({
 iconUrl: marker1,
 iconSize: [32,32]
})

const iconLocation = new Icon({
  iconUrl: marker2,
  iconSize: [32,32]
 })


const center = [46.227638, 2.213749];

const filterGare1 = dataGares.filter(
  (gare) => gare.fields.segmentdrg_libelle === "a"
);

const filterGare2 = dataGares.filter(
  (gare) =>
    (gare.fields.segmentdrg_libelle === "a" ||
    gare.fields.segmentdrg_libelle === "b") &&
    gare.fields.longitude_entreeprincipale_wgs84 !== undefined
);

const filterGare3 = dataGares.filter(
  (gare) =>
    (gare.fields.segmentdrg_libelle === "a" ||
    gare.fields.segmentdrg_libelle === "b" ||
    gare.fields.segmentdrg_libelle === "c") &&
    gare.fields.longitude_entreeprincipale_wgs84 !== undefined
);


async function getGareUser(gare) {
  try {
    if (tmp === 0){
      tmp = 60;
    }
    stringGare = "";
    console.log("time :"+tmp+", gare: "+gare);
    const response = await fetch(`http://localhost:5000/train/${gare}/${tmp}`);
    jsonData = await response.json();
    for (const e of jsonData) {
      stringGare = stringGare + e.goal.toString()+ ";";
    }
    
} catch (err){
    console.error(err.message);
} 

  let ret = markerRecherche();
  return ret;
}

function updateTextInput(val) {
  document.getElementById('textInput').value=val; 
}


function marker (filt){
  const map = filt.map(e => (
    <Marker
      key={e.recordid}
      position={[e.fields.latitude_entreeprincipale_wgs84, e.fields.longitude_entreeprincipale_wgs84]}
      onPopupOpen={() => console.log("Pop-up opened!")}   
      icon = {iconBasic} 
      >
      <Popup>
        <h3> {"Gare : " + e.fields.gare_alias_libelle_noncontraint} </h3>
        <RangeSlider></RangeSlider>
        <button onClick={() => getGareUser(e.fields.uic_code.slice(-8))}> Valider </button>
      </Popup>
  </Marker>
  ));

  return map;
}

function markerRecherche(){
  for (const it of dataGares) {
    if (stringGare.includes(it.fields.uic_code.slice(-8).toString()) && it.fields.longitude_entreeprincipale_wgs84 !== undefined){
      filter.push(it);
    }
  }
  console.log(filter);
 
  const mapR = filter.map(e => (
    <Marker
      key={e.recordid}
      position={[e.fields.latitude_entreeprincipale_wgs84, e.fields.longitude_entreeprincipale_wgs84]}
      icon = {iconBasic}
    >
      <Popup>
        <h3> {"Gare : " + e.fields.gare_alias_libelle_noncontraint} </h3>
        <button onClick={() => getGareUser(e.fields.uic_code.slice(-8))}> Valider </button>
        <RangeSlider></RangeSlider>

      </Popup>
    </Marker>
  ));
  console.log(mapR);
  filter = [];
  return mapR;
}


const ZoomTracker = ({ setZoom }) => {
  const map = useMapEvents({
    zoom(){
      setZoom(map.getZoom())
    }
  })
  return null
}

const ZoomControlledLayer = ({ zoom ,children }) => {
  let ret = null;
  if( stringGare == ""){
    if (zoom <= 9){
      console.log("niveau 1");
      ret = marker(filterGare1);
    } else if (zoom >= 10 && zoom <= 12){
      console.log("niveau 2");
      ret = marker(filterGare2);
    }else if (zoom >= 13){
      console.log("niveau 3");
      ret = marker(filterGare3);
    } else{
      console.log("Aucun niveau");
      ret = marker(filterGare1);
    }
  }  
  return (ret);
}

function LeafletgeoSearch() {
  const map = useMap(); //here use useMap hook

  useEffect(() => {

    const provider = new OpenStreetMapProvider({
      params: {
      'accept-language': 'fr', // Nom des villes en français
      countrycodes: 'fr', // Limite les recherches à la France seulement
    },
  });

    const searchControl = new GeoSearchControl({
      notFoundMessage: 'Désolé, cette adresse est introuvable.',
      provider,
      marker: {icon : iconLocation}
    });

    map.addControl(searchControl);

    return () => map.removeControl(searchControl)
  }, []);

  return null;
}


export default function Maps(){

  const [map, setMap] = useState(null);
  const [position, setPosition] = useState(null);

  
  const [, updateState] = React.useState();
  const forceUpdate = React.useCallback(() => updateState({}), []);

  useEffect(() => {
    if (!map) return;
    L.easyButton("fa-map-marker", () => {
      map.locate().on("locationfound", function (e) {
        setPosition(e.latlng);
        map.flyTo(e.latlng, 14);
      });
    }).addTo(map);
  }, [map]);

  const [zoom, setZoom] = useState()


  return (
    <>
    <MapContainer 
    center={center}
    className ="map"
    zoom={6}
    whenCreated={setMap}
    style={{ height: "87vh" }}
    >
      <TileLayer
        url="https://api.maptiler.com/maps/basic/256/{z}/{x}/{y}.png?key=F4ZxF5g8ioWE3GlTx3i0#-0.2/0.00000/76.51878"
        attribution='<a href="https://www.maptiler.com/copyright/" target="_blank">&copy; MapTiler</a> <a href="https://www.openstreetmap.org/copyright" target="_blank">&copy; OpenStreetMap contributors</a>'
      />
      <ZoomTracker setZoom={setZoom} />
      <ZoomControlledLayer zoom={zoom}>
      </ZoomControlledLayer>
      <LeafletgeoSearch provider={new OpenStreetMapProvider()} />
      <Button position="bottomright">Mon bouton</Button>
    </MapContainer>
    </>
  )

}
